function filtrarPosts(){var l,c,h,d,m,j,e;l=document.getElementById("buscaDePost");c=l.value.toUpperCase();h=document.getElementById("posts");d=document.getElementById("status");m=h.getElementsByTagName("li");for(e=0;e<m.length;e++){j=m[e].getElementsByTagName("a")[0];if(j){if(j.innerHTML.toUpperCase().indexOf(c)>-1){m[e].style.display=""}else{m[e].style.display="none"}}}posts=document.querySelectorAll("#posts > li");var b=[].slice.call(posts);var f=b.filter(function(i){return getComputedStyle(i).display==="none"});var g=b.filter(function(i){return getComputedStyle(i).display!=="none"});var a=f.length;var k=g.length;if(k>0){d.style.display="none"}else{d.style.display="block"}};